    private void btIMCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btIMCActionPerformed
        Double peso = Double.valueOf(tfPeso.getText());
        Double altura = Double.valueOf(tfAltura.getText());
        Double resultadoIMC = peso / (altura * altura) ;
        
        lbIMC.setText(String.format("Seu IMC Ã©: "
                + "%.1f", resultadoIMC));
    }//GEN-LAST:event_btIMCActionPerformed

